header {

    .header-wrapper{
        color: white;
        display: flex;
        justify-content: space-between;
        justify-items: stretch;
        padding: 0rem 2rem;
        width: 100%;
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        background: #1a1919c4;
        z-index: 1;

        @media screen and (max-width: 425px){
            padding: 0.2rem 0.1rem;
            align-items: center;
        }

        @media screen and (max-width: 768px){
            padding: 0.8rem 0.5rem;
            align-items: center;
        }

        @media screen and (min-width: 1441px){
            padding: 0.5rem 12rem;
        }

        & > * {
            display: flex;
            align-items: center;
        }

        .logo-block{
            flex: 0.3;
            justify-self: baseline;

            img{
                width: auto;
                height: auto;
            }

            @media screen and (max-width: 768px){
                flex: 0.4;
            }
        }

        nav {
            flex: 1;
            display: flex;
            flex-flow: column nowrap;
            align-items: center;

            .header__nav__menu-wrapper {
                width: 100%;
                display: flex;
                justify-content: space-around;
                .menu {
                    font-size: 0.95rem;
                    padding: 2rem 0;
    
                    @media screen and (max-width: 768px){
                        display: none;
                    }
                }
    
                
                .menu:hover{
                    &>.submenu {
                        animation: fadeInMenu 0.3s ease-in-out forwards;
                        display: block;
                        cursor: auto;
                    }
                }
    
                .submenu {
                    background: white;
                    position: absolute;
                    color: black;
                    padding: 1.5rem 3rem 0.5rem 0.5rem;
                    display: none;
    
                    .subitem {
                        cursor: pointer;
                        margin-bottom: 1rem;
                    }
                }

            }
            .submenu {
                display: none;
            }
        

        }
        .form-nav-block {
            flex: 0.3;
            font-size: 1.05em;
            justify-content: flex-end;

            .underline {
                width: 100%;
                margin-top: 0.6rem;
                border: 1.5px solid #6a65f8;
                background: #6a65f8;
            }

            @media screen and (max-width: 768px){
                display: none;
            }
        }

        .menu-bar-icon {
            display: none;

            @media screen and (max-width: 768px){
                display: block;
                font-size: 1.4rem;
                margin-right: 3%;
                padding: 0 0.2rem;
                cursor: pointer;
            }
        }

        .sidebar-768px-off {
            display: none;
        }

        .sidebar-768px-on {
            z-index: -10;
            position: absolute;
            top: 100%;
            right: 5%;
            left: 5%;
            margin: 0 auto;
            padding: 1rem;
            width: 85%;
            background: white;
            color: black;
            height: auto;
            

            .sidebar__menu {
                display: flex;
                flex-direction: column;

                .menu__item {
                    margin: 0.3rem 0;

                    a{
                        display: flex;
                        justify-content: space-between;
                    }

                    .submenu-off {
                        display: none;
                    }

                    .submenu-on {
                        display: flex;
                        flex-direction: column;
                        justify-content: space-around;
                        width: 100%;
                        padding: 1rem 1.5rem;
                        
                        .subitem { 
                            margin: 0.3rem 0;
                        }
                        
                    }
                }
            }
        }
    }
}

@keyframes fadeInMenu {
    from{
        opacity: 0;
        transform: translateY(4rem);
    }to{
        opacity: 1;
        transform: translateY(2rem);
    }
}

@keyframes rollershow {
    from{
        height: 0;
    }to{
        height: auto;
    }
}
